
SELECT play_type,yardline_100,quarter_seconds_remaining,half_seconds_remaining,game_seconds_remaining,CASE WHEN game_half = 'Half1' THEN 1 ELSE 0 END AS game_half_Half1,CASE WHEN game_half = 'Half2' THEN 1 ELSE 0 END AS game_half_Half2,CASE WHEN game_half = 'Overtime' THEN 1 ELSE 0 END AS game_half_overtime,drive,CASE WHEN qtr = 1 THEN 1 ELSE 0 END AS qtr_1,CASE WHEN qtr = 2 THEN 1 ELSE 0 END AS qtr_2,CASE WHEN qtr = 3 THEN 1 ELSE 0 END AS qtr_3,CASE WHEN qtr = 4 THEN 1 ELSE 0 END AS qtr_4,CASE WHEN qtr = 5 THEN 1 ELSE 0 END AS qtr_5,CASE WHEN down = 1 THEN 1 ELSE 0 END AS down_1,CASE WHEN down = 2 THEN 1 ELSE 0 END AS down_2,CASE WHEN down = 3 THEN 1 ELSE 0 END AS down_3,CASE WHEN down = 4 THEN 1 ELSE 0 END AS down_4,ydstogo,CASE WHEN no_huddle = 0 THEN 1 ELSE 0 END AS no_huddle_0,CASE WHEN no_huddle = 1 THEN 1 ELSE 0 END AS no_huddle_1,CASE WHEN posteam_timeouts_remaining = 0 THEN 1 ELSE 0 END AS posteam_timeouts_remaining_0,CASE WHEN posteam_timeouts_remaining = 1 THEN 1 ELSE 0 END AS posteam_timeouts_remaining_1,CASE WHEN posteam_timeouts_remaining = 2 THEN 1 ELSE 0 END AS posteam_timeouts_remaining_2,CASE WHEN posteam_timeouts_remaining = 3 THEN 1 ELSE 0 END AS posteam_timeouts_remaining_3,CASE WHEN defteam_timeouts_remaining = 0 THEN 1 ELSE 0 END AS defteam_timeouts_remaining_0,CASE WHEN defteam_timeouts_remaining = 1 THEN 1 ELSE 0 END AS defteam_timeouts_remaining_1,CASE WHEN defteam_timeouts_remaining = 2 THEN 1 ELSE 0 END AS defteam_timeouts_remaining_2,CASE WHEN defteam_timeouts_remaining = 3 THEN 1 ELSE 0 END AS defteam_timeouts_remaining_3,posteam_score,defteam_score,score_differential INTO TBL_ML_RP FROM TBL_NFLPLAYS_plays2 WHERE play_type IN ('run','pass')